import { ENV_CONFIG } from '../shared/env';
import { RuntimeInfo } from '../@types/global';
import HttpService from './http.service';

export class ApiService {
  baseApi;
  httpService;
  constructor(
    path = '',
    options = { baseUrl: ENV_CONFIG.apiUrl }
  ) {
    this.baseApi = `${options.baseUrl}/${path}`;
    this.httpService = new HttpService();
  }
  get(endpoint = '', params?) {
    return this.httpService.get(`${this.baseApi}${endpoint}`, params);
  }
  post(endpoint = '', payload?) {
    return this.httpService.post(`${this.baseApi}${endpoint}`, payload);
  }
  put(endpoint = '', payload?) {
    return this.httpService.put(`${this.baseApi}${endpoint}`, payload);
  }
  delete(endpoint = '', data?) {
    return this.httpService.delete(`${this.baseApi}${endpoint}`, data);
  }
}
