export const isDocker = !!process.env.RP_DOCKER;
export const isMacIntel =
    process.platform === 'darwin' && process.arch !== 'arm64'; // Intel
export const isLinux =
    process.platform === 'linux' &&
    process.arch !== 'arm64' &&
    process.arch !== 'arm'; // Linux
export const isLinuxArm =
    process.platform === 'linux' &&
    (process.arch === 'arm64' || process.arch === 'arm'); // Linux ARM
export const isMac = process.platform === 'darwin';
export const isWindows = process.platform === 'win32';
